function install(){
packages=$@
sudo apt-get install${=packages}&& echo"$packages" >> ~/.installd}
apt_pref_compdef install"install"function add-repo(){
repos=$@
sudo add-apt-repository -y ppa:${=repos}&& echo"sudo add-apt-repository -y ppa:$repos" >> ~/.installd
sudo apt-get update}

#!/usr/bin/env ruby
require'miro'
require'listen'# set defaults
@conky_rc_file='/home/coda/.conky/conkyrc'
@conky_lua_file='/home/coda/.conky/conky.lua'
watch_dir='/home/coda/Wallpapers'# miro optionsMiro.options[:color_count]=6Miro.options[:resolution]='1500x1500'defget_colors(new_wallpaper)
puts"getting colors for#{new_wallpaper}"
colors=Miro::DominantColors.new(new_wallpaper).to_hex.sort
@conky_txt_color_default= colors[1][1..-1]
@conky_bg_color_default= colors[0][1..-1]
@conky_fg_color_default= colors[3][1..-1]enddefreplace_colors_all text, bg, fg
system("sed '-i' 's/default_color .*$/default_color#{text}/'#{@conky_rc_file}")
system("sed '-i' 's/color_bg .*$/color_bg = 0x#{bg}/'#{@conky_lua_file}")
system("sed '-i' 's/color_fg .*$/color_fg = 0x#{fg}/'#{@conky_lua_file}")end
listener=Listen.to(watch_dir)do|modified, added, removed|if added.last
get_colors(added.last)
replace_colors_all(@conky_txt_color_default, @conky_bg_color_default, @conky_fg_color_default)endend
listener.start
sleep

