cd ~/$1;}#find a file with the string in the namefunction ff(){ find . -name'*'$1'*' ;}#find a file with the string in the name and exec $2 on itfunction fe(){ find . -name'*'$1'*' -exec $2{}\; ;}#find a file ending in $2 and search for string $1 in itfunction fstr()# find a string in a set of files{if[ $# -ne2];then
echo"Usage: fstr \"pattern\" [files] "return;fi
SMSO=$(tput smso)
RMSO=$(tput rmso)
find . -type f -name"*${2}" -print | xargs grep -sin"$1" |\ sed"s/$1/$SMSO$1$RMSO/gI"}#remove spaces from filenamefunction nospace(){
mv"$1""`echo$1 | tr ' ' '_'`"}#filenames to lowercasefunction lowercase(){for file ;do
filename=${file##*/}case"$filename" in
*/*) dirname==${file%/*} ;;
*) dirname=.;;esac
nf=$(echo $filename | tr A-Z a-z)
newname="${dirname}/${nf}"if["$nf" !="$filename"];then
mv"$file""$newname"else
echo"lowercase:$file not changed."fidone}## Get the commands and aliases###list all the aliasesfunction list_aliases{
ALIASES=`alias | cut -d'=' -f 1`
echo"$ALIASES" | sort -u}#list all the commandsfunction list_commands{
COMMANDS=`echo -n $PATH | xargs -d : -I{} find{} -maxdepth1\ -executable -type f -printf'%P\n'`
echo"$COMMANDS" | sort -u}#find an aliasfunction lag{for a in$(list_aliases | grep $1);do which $a;done}#find a commandfunction lcg{for c in$(list_commands | grep $1);do which $c;done}## Git & Deployment###ship the current branchfunction ship(){
