$(document).ready(function(){// Hide the boxes that are not the first box$('.box').slice(1).hide();$(".section a[href^='http://']").attr("target","_blank");// Title toggles the view of the content box$(".title > a").click(function(){$("content").slideToggle('fast');
});// Initial variablesvarLAT=$('.box').first().data('lat'),LNG=$('.box').first().data('long'),ZM_OUT=4,ZM_IN=20;// Set the initial mapmap=newGMaps({div:'#map',zoom:ZM_OUT,mapTypeId:google.maps.MapTypeId.HYBRID,lat:LAT,lng:LNG
});setTimeout(function() {map.addMarker({lat:LAT,lng:LNG, });},1250);setTimeout(function() {map.setZoom(ZM_IN);},2500);// Previous_event clicks should be hidden on the first box, the rest should run the previous transition$(".previous_event").click(prevTransition);$('.box').first().children('.previous_event').hide();// Next event should run, and reloop if it is the last box$(".next_event").click(nextTransition);$('.box').last().children(".next_event").click(reloopTransition);functionnextTransition() {$('.nav a').addClass('disabled');TRANS=$(this).parents('.box').data('transition');if(TRANS=="bigTrans")ZM_OUT=4;if(TRANS=="medTrans")ZM_OUT=8;if(TRANS=="smallTrans")ZM_OUT=16;if(TRANS=="microTrans")ZM_OUT=20;map.setZoom(ZM_OUT);$(this).parents(".box").slideToggle('slow',function(){$(this).next().slideToggle('slow',function(){varLAT=$(this).data('lat'),LNG=$(this).data('long');setTimeout(function() {map.addMarker({lat:LAT,lng:LNG, });},750);setTimeout(function() {map.setCenter(map.markers[map.markers.length-1].getPosition().lat(),map.markers[map.markers.length-1].getPosition().lng());},1200);setTimeout(function() {map.setZoom(ZM_IN);},3750);setTimeout(function() {$('.nav a').removeClass('disabled')},4000);
});
});
};functionprevTransition() {$('a').addClass('disabled');TRANS=$(this).parents('.box').prev().data('transition');if(TRANS=="bigTrans")ZM_OUT=4;if(TRANS=="medTrans")ZM_OUT=8;if(TRANS=="smallTrans")ZM_OUT=16;if(TRANS=="microTrans")ZM_OUT=20;map.setZoom(ZM_OUT);$(this).parents(".box").slideToggle('slow',function(){$(this).prev().slideToggle('slow',function(){varLAT=$(this).data('lat'),LNG=$(this).data('long');setTimeout(function() {map.addMarker({lat:LAT,lng:LNG, });},750);setTimeout(function() {map.setCenter(map.markers[map.markers.length-1].getPosition().lat(),map.markers[map.markers.length-1].getPosition().lng());},1200);setTimeout(function() {map.setZoom(ZM_IN); },3750);setTimeout(function() {$('a').removeClass('disabled')},4500);
});
});
};functionreloopTransition() {ZM_OUT=4;map.setZoom(ZM_OUT);$(this).parents(".box").slideToggle('slow',function(){$(this).siblings(":first").slideToggle('slow',function(){varLAT=$(this).data('lat'),LNG=$(this).data('long');setTimeout(function() {map.addMarker({lat:LAT,lng:LNG, });},750);setTimeout(function() {map.setCenter(map.markers[map.markers.length-1].getPosition().lat(),map.markers[map.markers.length-1].getPosition().lng());},1200);setTimeout(function() {map.setZoom(ZM_IN); },3750);setTimeout(function() {$('a').removeClass('disabled')},4500);
});
});
};
